import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Container, Row, Col, Form, Button, Alert } from "react-bootstrap";
import { FaInstagram } from "react-icons/fa";

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [validated, setValidated] = useState(false);
    const [error, setError] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        const form = event.currentTarget;
        if (form.checkValidity() === false) {
            event.stopPropagation();
        } else {
            // Ajouter ici la logique de connexion
            console.log("Username:", username);
            console.log("Password:", password);
            // Réinitialiser les champs de saisie
            setUsername("");
            setPassword("");
          
            setValidated(false);
            setError(null);
        }
        setValidated(true);
    };

    return (
        <Container fluid className="bg-light min-vh-100 d-flex flex-column">
            <Row className="flex-grow-1">
                <Col md={{ span: 4, offset: 4 }} className="my-auto">
                    <div className="bg-white border rounded p-5">
                        <FaInstagram size={48} className="mb-4 text-danger" />
                        <Form noValidate validated={validated} onSubmit={handleSubmit}>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Control
                                    required
                                    type="text"
                                    placeholder="Nom d'utilisateur"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                />
                                <Form.Control.Feedback type="invalid">
                                    Veuillez entrer un nom d'utilisateur valide.
                                </Form.Control.Feedback>
                            </Form.Group>

                            <Form.Group controlId="formBasicPassword">
                                <Form.Control
                                    required
                                    type="password"
                                    placeholder="Mot de passe"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                />
                                <Form.Control.Feedback type="invalid">
                                    Veuillez entrer un mot de passe valide.
                                </Form.Control.Feedback>
                            </Form.Group>
                            <Button variant="primary" type="submit" className="w-100 mt-3">
                                Se connecter
                            </Button>
                        </Form>
                    </div>
                    <div className="mt-3 text-center">
                        <a href="/">Mot de passe oublié ?</a>
                    </div>
                </Col>
            </Row>
            <Row>
                <Col className="text-center mt-3">
                    Vous n'avez pas de compte ? <a href="/">Inscrivez-vous</a>
                </Col>
            </Row>
        </Container>
    );
};

export default Login;
