import React, { useState } from 'react';
import Client from './Client';
import ClientForm from './ClientForm';
const App = (props) => {

    const [clients, setClients] = useState("")



    const HandleDelete = (id) => {
        const updatedClient = [...clients]
        const index = updatedClient.findIndex(client => client.id === id)
        updatedClient.splice(index, 1)
        setClients(updatedClient)
    }

    const handleAdd = (client) => {
        if (clients === "") {
            alert("champ vide")
        }
        const updatedClient = [...clients]
        updatedClient.push(client)
        setClients(updatedClient)
    }


    return (
        <div>

            {clients.map(client =>
                <Client
                    key={client.id}
                    details={client}
                    onDelete={HandleDelete}
                />
            )}


            <ClientForm onClientAdd={handleAdd} />

        </div>
    );
};

export default App;
