import React from 'react';
import Navbare from './Navbare';
import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Card from 'react-bootstrap/Card';
import axios from 'axios';
const Accueil = () => {


    const userId = sessionStorage.getItem('userId');
    const username = sessionStorage.getItem('username');


    const [selectedImage, setSelectedImage] = useState(null);
    const [pub, setPub] = useState("");
    const [pubs, setPubs] = useState([]);




    useEffect(() => {
        afficherPub();
    }, [pubs]);

    const afficherPub = async () => {
        try {
            const response = await axios.post('http://localhost:8000/makapub');
            setPubs(response.data);
        } catch (error) {
            console.error("Erreur lors de la récupération de la publication :", error);
        }
    };



    const publier = (e) => {

        e.preventDefault();
        console.log(pub);
        try {
            axios.post('http://localhost:8000/publication', {
                pub: pub,
                sender_id: userId
            }).then((response) => {
                setPub("")
                if (response.data.success) {
                    toast.success('Bien publiée', {
                        position: "top-center",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        theme: "light",
                    });
                }

            }).catch(() => {

            })
        } catch (error) {

        }

    }
    return (
        <div>
          <div style={{ marginTop: '100px' }}></div>
          <Navbare />
      
          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh' }}>
            <div>
              <h2>Créer une publication</h2>
              {/* ... */}
              <button onClick={afficherPub}>test</button>
            </div>
      
            <div>
              {pubs.map((publication, index) => (
                <Card>
                  {/* ... */}
                </Card>
              ))}
            </div>
          </div>
        </div>
      );
      
};

export default Accueil;
