import React, { useState } from 'react';

const ClientForm = () => {

    const [nouveauClients, setNouveauclients] = useState("")
    const HandleSubmit = (e) => {
        e.preventDefault();
        const id = new Date().getTime()
        const nom = nouveauClients
        props.onClientAdd({ id, nom })
        setNouveauclients("")
    }

    const handleChange = (e) => {
        setNouveauclients(e.currentTarget.value)
    }
    return (
        <div>
            <form onSubmit={HandleSubmit}>
                <input
                    value={nouveauClients}
                    onChange={handleChange}
                />

            </form>
        </div>
    );
};

export default ClientForm;
