import React, { useState } from 'react';
import Navigation from '../navigation/Navigation';
const Compteur = () => {
    const [count, setCount] = useState(0)
    const [clients, setClient] = useState(
        [
            { id: 1, nom: "Marco" },
            { id: 2, nom: "Polo" },
            { id: 3, nom: "Rakoto" },
            { id: 4, nom: "BG" },
        ]
    )
    const handleDelelte = (id) => {
        const [uptadeClient] = [...clients]
        const index = uptadeClient.findIndex((client) => client.id === id);
        uptadeClient.splice(index, 1)
        setClient(uptadeClient)
    }
    return (
        <div>
            <h3>Compteur</h3>
            <Navigation />
            {count} <button onClick={() => setCount(count + 1)}>Incrementer</button>
            <h3>Affichage</h3>
            <p>
                <div>
                    {clients.map((x) => <li>  {x.id} {x.nom} <button onClick={() => { console.log("sd") }}>X</button></li>)}


                    <input type="text" placeholder='Entrer un client' /><button>Ajouter</button>
                </div>
            </p>

        </div >
    );
};

export default Compteur;
