import React, { useState } from 'react';
import axios from 'axios';
const App = (props) => {
    const [clients, setClients] = useState([
        { id: 1, nom: "RAKOTO" },
        { id: 2, nom: "DAVID" },
        { id: 3, nom: "JACK" },
        { id: 4, nom: "DIG" },

    ])

    const mamafa = (id) => {
        const tavela = clients.filter((client) => client.id != id)
        setClients(tavela)
    }
    const [nouveauClient, setNouveau] = useState("")
    const handleSubmit = (e) => {
        e.preventDefault()
        const copyClient = [...clients]
        const id = new Date().getTime()
        const nom = nouveauClient
        copyClient.push({ id: id, nom: nom })
        setClients(copyClient)
        setNouveau("")
        axios.post('/newitem', copyClient)
        console.log(copyClient);
    }
    const handleChange = (e) => {
        setNouveau(e.currentTarget.value)
    }

    return (
        <div>
            <form action="" onSubmit={handleSubmit}>
                <p> {clients.map((client) => <li>{client.nom} <button onClick={() => mamafa(client.id)}>X</button></li>)} </p>
                <input onChange={handleChange} type="text" value={nouveauClient} placeholder='Entrer nouveau client'

                /> <button>Ajouter +</button>
            </form>
        </div >
    );
};

export default App;
