import React from 'react';
import axios from 'axios';
import { useState } from 'react';
const AddLivre = () => {

    const [item, setItem] = useState({
        title: "",
        description: ""
    })
    const handleChange = (e) => {
        setItem(e.target.value)
    }
    const add = (e) => {

        const newItem = {
            title: item.title,
            description: item.description
        }

        axios.post('/newitem', newItem)
        console.log(newItem);
        setItem({
            title: "",
            description: ""
        })
    }
    return (
        <div>

            <input id='ti' onChange={handleChange} type="text" name="title" value={item.title} placeholder='Entrer Title' />
            <input id='des' onChange={handleChange} type="text" value={item.description} name="description" placeholder='Entrer description' />
            <button onClick={add}>Ajouter</button>
        </div>
    );
};

export default AddLivre;
