import React, { useState } from 'react';
import Navigation from '../navigation/Navigation';
const Compteur = () => {
    const [count, setCount] = useState(0)
    const [client, setClient] = useState(
        [
            { id: 1, nom: "Marco" },
            { id: 2, nom: "Polo" },
            { id: 3, nom: "Rakoto" },
            { id: 4, nom: "BG" },
        ]
    )
    return (
        <div>
            <h3>Compteur</h3>
            <Navigation />
            {count} <button onClick={() => setCount(count + 1)}>Incrementer</button>
            <h3>Affichage</h3>
            <p>
                {client.map((x) => <li>{x.nom}</li>)} <button>X</button>

            </p>

        </div>
    );
};

export default Compteur;
