import React, { useState } from 'react';
import Navigation from '../navigation/Navigation';
import "../style.css"
const Calculatrice = () => {
    const [soratra, setSoratra] = useState("")
    const [a, setA] = useState("")
    const [valiny, setValiny] = useState("")
    const Surp = () => {
        setA("Foky eeh")
    }
    const handleClick = (e) => {
        setValiny(valiny.concat(e.currentTarget.id))
    }
    const handelChange = (e) => {

        setSoratra(soratra)
    }
    const calculer = () => {
        setValiny(eval(valiny))
    }
    const mamafa = () => {
        setValiny("")
    }
    const fafa = () => {
        setValiny(valiny.slice(0, valiny.length - 1))
    }

    return (
        <div>

            <h3>Calculatrice</h3>
            <Navigation />
            <div className="vatana">
                <h3 className="resultat"> {valiny}  <button onClick={fafa} id='x'>X</button></h3>
                <button onClick={handleClick} className="btn" id='8'>8</button>
                <button onClick={handleClick} className="btn" id='7'>7</button>
                <button onClick={handleClick} className="btn" id='6'>6</button>
                <button onClick={handleClick} className="btn" id='5'>5</button>
                <button onClick={handleClick} className="btn" id='4'>4</button>
                <button onClick={handleClick} className="btn" id='3'>3</button>
                <button onClick={handleClick} className="btn" id='2'>2</button>
                <button onClick={handleClick} className="btn" id='1'>1</button>
                <button onClick={handleClick} className="btn" id='0'>0</button>
                <button onClick={handleClick} className="btn" id='.'>.</button>
                <button onClick={handleClick} className="btn" id='-'>-</button>
                <button onClick={handleClick} className="btn" id='+'>+</button>
                <button onClick={handleClick} className="btn" id='/'>/</button>
                <button onClick={handleClick} className="btn" id='*'>*</button>
                <button className="btn" onClick={mamafa} id='clear'>C</button>
                <button className="btn" onClick={calculer} id='equal'>=</button>
            </div>
            <input type="text" onClick={handelChange} value={soratra} /> <button onClick={Surp}>Surprise</button>
        </div>
    );
};

export default Calculatrice;
