import React, { useState } from 'react';
import { Container, Form, Button } from 'react-bootstrap';

function Inscription() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleConfirmPasswordChange = (event) => {
    setConfirmPassword(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Effectuez ici les étapes de validation et de soumission des données d'inscription
    // par exemple, envoyer les données à un serveur backend pour créer un nouvel utilisateur

    console.log('Nom d\'utilisateur :', username);
    console.log('Mot de passe :', password);
    console.log('Confirmer le mot de passe :', confirmPassword);

    // Réinitialisez les champs après la soumission réussie
    setUsername('');
    setPassword('');
    setConfirmPassword('');
  };

  return (
    <Container>
      <h2>Inscription</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="formUsername">
          <Form.Label>Nom d'utilisateur:</Form.Label>
          <Form.Control
            type="text"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Mot de passe:</Form.Label>
          <Form.Control
            type="password"
            value={password}
            onChange={handlePasswordChange}
            required
          />
        </Form.Group>
        <Form.Group controlId="formConfirmPassword">
          <Form.Label>Confirmer le mot de passe:</Form.Label>
          <Form.Control
            type="password"
            value={confirmPassword}
            onChange={handleConfirmPasswordChange}
            required
          />
        </Form.Group>
        <Button variant="primary" type="submit">
          S'inscrire
        </Button>
      </Form>
    </Container>
  );
}

export default Inscription;
