import React, { useState } from 'react';
import Navigation from '../nav/Navigation';
import '../style.css'
const Calculatrice = () => {
    const [valiny, setValiny] = useState("")
    const handleClick = (e) => {
        setValiny(valiny.concat(e.currentTarget.id))
    }
    const mamafabe = (e) => {
        setValiny("")
    }
    const mamafakely = (e) => {
        setValiny(valiny.slice(0, valiny.length - 1))
    }
    const calculer = () => {
        setValiny(eval(valiny))
    }
    return (
        <div>
            <h3>Calculatrice</h3>
            <Navigation />
            <div className="vatana">
                <h3 className="result">{valiny}</h3>
                <button className="btn" onClick={handleClick} id="9">9</button>
                <button className="btn" onClick={handleClick} id="8">8</button>
                <button className="btn" onClick={handleClick} id="7">7</button>
                <button className="btn" onClick={handleClick} id="6">6</button>
                <button className="btn" onClick={handleClick} id="5">5</button>
                <button className="btn" onClick={handleClick} id="4">4</button>
                <button className="btn" onClick={handleClick} id="3">3</button>
                <button className="btn" onClick={handleClick} id="2">2</button>
                <button className="btn" onClick={handleClick} id="1">1</button>
                <button className="btn" onClick={handleClick} id="0">0</button>
                <button className="btn" onClick={mamafabe} id="clear">C</button>
                <button className="btn" onClick={mamafakely} id="mamafa">X</button>
                <button className="btn" onClick={handleClick} id=".">.</button>
                <button className="btn" onClick={handleClick} id="+">+</button>
                <button className="btn" onClick={handleClick} id="-">-</button>
                <button className="btn" onClick={handleClick} id="*">*</button>
                <button className="btn" onClick={handleClick} id="/">/</button>
                <button className="btn" onClick={calculer} id="equal">=</button>
            </div>

        </div>
    );
};

export default Calculatrice;
