import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';
import Create from './Message';


const userId = sessionStorage.getItem('userId');
const username = sessionStorage.getItem('username');

const Message = () => {
  const [message, setMessage] = useState('');
  const [mess, setMess] = useState([]);
  const [user, setUser] = useState(null);
  const [list, setList] = useState([]);
  const [temp, setTemp] = useState('');
  const [anarana,setAnarana] =useState('')

  const handleChange = (e) => {
    setMessage(e.target.value);
  };

  useEffect(() => {
    const fetchUser = async () => {

      try {
        const response = await axios.get(`http://localhost:8000/getUser/${userId}`);
        setList(response.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchUser();
  }, [mess]);

  const afficher = (id,ana) => {
    setAnarana(ana)
    setTemp(id);
    setUser(id);
    fetchMessages(id);//id andefasana 
  };

  const fetchMessages = async (id) => {
    alert(id)
    try {

      const response = await axios.get(`http://localhost:8000/message/:id`);
      setMess(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };



  const sendMessage = async (e) => {
    setTemp(e.target.value);

    const recipient_id = temp;
    const content = message;

    try {
      const response = await axios.post('http://localhost:8000/message', {
        sender_id: userId,
        recipient_id: recipient_id,
        content: content,
      });
      if (response.data.success) {
        setMessage('');
      }
      console.log('Le message a été envoyé:', message);
    } catch (error) {
      console.error(error);
    }
  };

  // useEffect(() => {
  //   if (user) {
  //     fetchMessages(user);
  //   }
  // }, [user]);

  return (

    <div className="container-fluid">
      <div className="row">
        <div className="col-8 mx-auto my-auto text-center">
          <div style={{ marginTop: '100px' }}></div>
          <Navbare />
          <div style={{ position: 'fixed' }}>
            <h3>Message</h3>
            <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="1"></textarea>{' '}

            <br />
            <button onClick={sendMessage}>Envoyer</button> <br />
            <input type="hidden" value={temp} />
          </div>

          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ borderRadius: '100px' }}>
            <input style={{position:'fixed',marginTop:'-310px'}} value={anarana} type="text" />
              {mess.map((m) => (
                <div key={m.id}>
                  <p></p>
                  <p style={{ background: 'blue', color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center' }}>
                    {m.content}
                  </p>
                  <p style={{ fontSize: '14px' }}>{m.createdAt}</p>
                </div>
              ))}
            </div>
          </div>


        </div>

        <div className="col-4">
          <div style={{ marginTop: '100px' }}></div>
          <div style={{position:'fixed'}}>
            <h3>Liste des utilisateurs</h3>
            {list.map((user) => (
              <li onClick={() => afficher(user.id,user.login)} key={user.id}> {user.id} - {user.login}</li>
            ))}
          </div>

        </div>
      </div>
    </div>


  );
};

export default Message;
