import React, { useState } from 'react';
import Axios from 'axios';
import { NavLink } from 'react-router-dom';


const Create = () => {
    const [list, setList] = useState([])
    const [nom, setNom] = useState("")
    const [prenom, setPrenom] = useState("")
    const [date, setDate] = useState("")
    const [age, setAge] = useState(0)


    const HandleChangenom = (e) => {
        setNom(e.target.value)
    }

    const HandleChangeprenom = (e) => {
        setPrenom(e.target.value)
    }
    const HandleChangeage = (e) => {
        setAge(e.target.value)
    }
    const HandleChangedate = (e) => {
        setDate(e.target.value)
    }

    const addPers = () => {
        Axios.post('http://localhost:8000/create',
            {
                nom: nom,
                prenom: prenom,
                date: date,
                age: age,

            });
        try {
            setNom("")
            setPrenom("")
            setAge("")

            alert("success")
        } catch (error) {
            alert(error)
        }
    }
    const showPers = () => {
        Axios.get('http://localhost:8000/afficher').then((response) => {
            setList(response.data)
            //  console.log(response.data);

        });
        try {
            console.log("ok");
        } catch (error) {
            console.log(error);
        }
    }
    const mamafa = (id) => {
        alert(id)
        Axios.delete("http://localhost:8000/delete/${id}").then((err, res) => {
            console.log(res.data);
        })
    }
    return (
        <div>
            <h3>CREATE</h3>
            <NavLink to='/'>Accueil</NavLink> <br />
            <input type="text" placeholder='Nom' value={nom} onChange={HandleChangenom} name='nom' />
            <input type="text" placeholder='Prenom' value={prenom} onChange={HandleChangeprenom} name='prenom' />
            <input type="date" placeholder='' value={date} onChange={HandleChangedate} name='date' />
            <input type="number" placeholder='Age' value={age} onChange={HandleChangeage} name='age' /><br />
            <button onClick={addPers}>Ajouter</button><br />
            <button onClick={showPers}>Show</button>

            {list.map((v) =>
                <div key={v.id}>
                    <h2>Nom:{v.nom}   Prenom:{v.prenom} Age:{v.age}</h2> <button onClick={() => { mamafa(v.id) }} className="delete">delete</button><button className="update">update</button>
                </div>


            )}
        </div >
    );
};

export default Create;
