import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import Button from 'react-bootstrap/Button';
import { Form } from 'react-bootstrap';
import Axios from 'axios';
import { Table } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.css"


const Users = () => {
    const [list, setList] = useState([])
    const [username, setUsername] = useState("")
    const [password, setPass] = useState("")
    const handleChange = (e) => {
        setUsername(e.target.value)
    }
    const Enregister = () => {
        if (username != '' && password != '') {
            Axios.post("http://localhost:8000/Enregister", {
                username: username,
                password: password
            }
            ).then(() => {
                setPass("")
                setUsername("")
                alert("Enregister")
            }

            ).catch((err) => {
                console.log(err);
            })
        } else {
            alert("Remplir les champs vide")
        }

    }



    const login = () => {

    }


    useEffect(() => {
        Axios.get("http://localhost:8000/login").then((response) => {
            setList(response.data)

        })

    })
    const notify = () => {
        toast.success('ðŸ¦„ Wow so easy!', {
            position: "top-center",
            theme: "dark",
        });
    }

    return (
        <div>
            <div className="container">
                <h3>Inscrire</h3>
                <Form.Control size="lg" value={username} type="text" onChange={handleChange} placeholder="Username" style={{ width: 500 }} />
                <br />
                <Form.Control size="lg" value={password} type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} style={{ width: 500 }} /> <br />
                <Button onClick={Enregister} variant="info">S'inscrire</Button>
            </div>
            <div className="container"> <p>---------------------------------****************---------------------------------</p></div>
            <div className="container">
                <h3>Login</h3>
                <Form.Control size="lg" value={username} type="text" onChange={handleChange} placeholder="Username" style={{ width: 500 }} />
                <br />
                <Form.Control size="lg" value={password} type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} style={{ width: 500 }} /> <br />
                <Button onClick={Enregister} variant="info">Connecter</Button>
            </div>

            <Table striped bordered hover size="sm">
                <thead>
                    <tr>
                        <th>Login</th>
                        <th>Password</th>

                    </tr>

                    {list.map((l) => (
                        <tr>
                            <td>{l.user}<input value={l.user} type="hidden" /></td>
                            <td>{l.pass}</td>

                        </tr>
                    )
                    )}

                </thead>

            </Table>
            <button onClick={notify}>Notify</button>
        </div>
    );
};

export default Users;
