import React, { useState } from 'react';
import { Navbar, Nav, Container, Form } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import Deconnexion from './Deconnexion';



const userId = sessionStorage.getItem('userId');
const username = sessionStorage.getItem('username');

const NavbareAdmin = () => {
  const [sessionData, setSessionData] = useState(null);

  const handleClick = () => {
    const response = Deconnexion.deco();
    console.log(response);

  };

  useEffect(() => {
    // Vérifier l'authentification ici
    const isAuthenticated = sessionStorage.getItem("userId") !== null;

    if (!isAuthenticated) {
      // Rediriger vers la page de connexion si l'utilisateur n'est pas authentifié
      // Vous pouvez ajuster le chemin de redirection en fonction de votre configuration
      return <Redirect to="/login" />;
    }
  }, []);


  return (
    <div>

      <Navbar bg="light" expand="lg" fixed="top">
        <Container>
          <Navbar.Brand href="#" style={{ color: 'blue', fontSize: '40px', marginRight: '600px' }}>
            E-Tafa  {username}
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <NavLink
                to="/accueil"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Accueil
              </NavLink>


              <NavLink
                to="/create"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Ban
              </NavLink>

              <NavLink
                to="/message"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Message
              </NavLink>
              <NavLink
                to="/contact"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Contact
              </NavLink>
              <NavLink
                to="/about"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                A propos
              </NavLink>

              <button onClick={handleClick}>Déconnexion</button>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

      {sessionData && ( // afficher les données de session si elles sont disponibles
        <div>
          <p>Nom d'utilisateur : {sessionData.username}</p>
          <p>Email : {sessionData.email}</p>
          {/* Ajoutez d'autres champs de données de session selon vos besoins */}
        </div>
      )}

    </div>
  );
};

export default NavbareAdmin;
