import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { Form } from 'react-bootstrap';
import Axios from 'axios';
const Users = () => {

    const [username, setUsername] = useState("")
    const [password, setPass] = useState("")
    const handleChange = (e) => {
        setUsername(e.target.value)
    }
    const Enregister = () => {
        if (username != '' && password != '') {
            Axios.post("http://localhost:8000/Enregister", {
                username: username,
                password: password
            }
            ).then(() => {
                setPass("")
                setUsername("")
                alert("Enregister")
            }

            ).catch((err) => {
                console.log(err);
            })
        } else {
            alert("Remplir les champs vide")
        }

    }

    return (
        <div>
            <div className="container">
                <h3>Inscrire</h3>
                <Form.Control size="lg" value={username} type="text" onChange={handleChange} placeholder="Username" style={{ width: 500 }} />
                <br />
                <Form.Control size="lg" value={password} type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} style={{ width: 500 }} /> <br />
                <Button onClick={Enregister} variant="info">S'inscrire</Button>
            </div>
            <p>--------------------------------------------------------------------</p>
            <div className="container">
                <h3>Inscrire</h3>
                <Form.Control size="lg" value={username} type="text" onChange={handleChange} placeholder="Username" style={{ width: 500 }} />
                <br />
                <Form.Control size="lg" value={password} type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} style={{ width: 500 }} /> <br />
                <Button onClick={Enregister} variant="info">S'inscrire</Button>
            </div>
        </div>
    );
};

export default Users;
