import React, { useState } from 'react';

const App = (props) => {
    const [clients, setClients] = useState([
        { id: 1, nom: "RAKOTO" },
        { id: 2, nom: "DAVID" },
        { id: 3, nom: "JACK" },
        { id: 4, nom: "DIG" },

    ])
    const handleSubmit = (e) => {
        e.preventDefault()
        const copyClient = [...clients]
        const id = new Date().getTime()
        const nom = nouveauClient
        copyClient.push({ id: id, nom: nouveauClient })
        setClients(copyClient)
        setNouveauClient("")
    }
    const handleChange = (e) => {
        setNouveauClient(e.currentTarget.value)
    }
    const [nouveauClient, setNouveauClient] = useState("")
    return (
        <div>
            <p> {clients.map((client) => <li>{client.nom} <button>X</button></li>)} </p>
            <input onChange={handleChange} onSubmit={handleSubmit} type="text" value={nouveauClient} placeholder='Entrer nouveau client'

            /> <button>Ajouter +</button>
        </div >
    );
};

export default App;
