import React, { useState, useEffect } from 'react';
import Navbare from './Navbare';
import axios from 'axios';

const Message = () => {

    const [message, setMessage] = useState('');
    const [mess, setMess] = useState([]);

    const handleChange = (e) => {
        setMessage(e.target.value)
    }

    const sendMessage = async () => {
        const sender_id = 1;
        const recipient_id = 4;
        const content = message;

        try {
            const response = await axios.post('http://localhost:8000/message',{sender_id,recipient_id,content});
            console.log('Le message a été envoyé:', response.data);
            setMessage('');
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        const fetchMessages = async () => {
          try {
            const response = await axios.get('http://localhost:8000/message/4');
            setMess(response.data);
            console.log(response.data);
          } catch (error) {
            console.error(error);
          }
        };
        fetchMessages();
      }, []);

    return (
        <div>
            <div style={{ marginTop: '100px' }}></div>
            <Navbare />
            <h3>Message</h3>

            <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="10"></textarea> <br />
            <button onClick={sendMessage}>Envoyer</button>
            
            <div>
                {mess.length > 0 ? (
                    mess.map((m) => (
                        <div key={m.id}>
                            <p style={{background:"blue", color:'white',height:'50px',width:'500px'}}> {m.content}</p>
                        </div>
                    ))
                ) : (
                    <p>Pas de messages disponibles.</p>
                )}
            </div>

        </div>
    );
};

export default Message;
