import React from 'react';
import Navbare from './Navbare';
import axios from 'axios';
const Message = () => {
const[message,setMessage]=useState
    const sendMessage = async (senderId, recipientId, content) => {

        try {
            const response = await axios.post('http://localhost:8000/message', {
                sender_id: senderId,
                recipient_id: recipientId,
                content: content
            });
            console.log('Le message a été envoyé:', response.data);
        } catch (error) {
            console.error(error);
        }
    };

    sendMessage(1, 4, 'Bonjour, comment ça va ?');

    return (
        <div>
            <div style={{ marginTop: '100px' }}></div>
            <Navbare />
            <h3>Message</h3>
           


               <textarea value={message} name="" id="" cols="30" rows="10"></textarea> <br />
                <button>Envoyer </button>





            
        </div>
    );
};

export default Message;
