import React, { useState } from 'react';
import axios from 'axios';
import { NavLink } from 'react-router-dom';

const Create = () => {

    const [nom, setNom] = useState("")
    const [prenom, setPrenom] = useState("")
    const [age, setAge] = useState("")

    const HandleChange = (e) => {
        setNom(e.target.value)
    }
    const HandleSubmit = async (event) => {

        event.preventDefault();
        try {
            await axios.post("http://localhost:8000/books", nom)

        } catch (err) {
            console.log(err);
        }


    }
    return (
        <div>
            <h3>CREATE</h3>
            <NavLink to='/'>Accueil</NavLink> <br />
            <input type="text" placeholder='Nom' value={nom} onChange={HandleChange} name='nom' />
            <input type="text" placeholder='Prenom' value={prenom} onChange={HandleChange} name='prenom' />
            <input type="number" placeholder='Age' value={age} onChange={HandleChange} name='age' /><br />
            <button onClick={HandleSubmit}>Ajouter</button>
        </div >
    );
};

export default Create;
