import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { NavLink } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Table } from 'react-bootstrap';




const Create = () => {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [list, setList] = useState([])
    const [nom, setNom] = useState("")
    const [prenom, setPrenom] = useState("")
    const [date, setDate] = useState("")
    const [age, setAge] = useState()

    useEffect(() => {
        Axios.get("http://localhost:8000/afficher").then((response) => {
            setList(response.data)
            console.log(response.data);
        })

    })

    const HandleChangenom = (e) => {
        setNom(e.target.value)
    }

    const HandleChangeprenom = (e) => {
        setPrenom(e.target.value)
    }
    const HandleChangeage = (e) => {
        setAge(e.target.value)
    }
    const HandleChangedate = (e) => {
        setDate(e.target.value)
    }

    const [editNom, seteditNom] = useState("")
    const [editPrenom, seteditPrenom] = useState("")
    const [editAge, seteditAge] = useState()
    const [editId, seteditId] = useState()

    const addPers = () => {
        if (!nom || !prenom || !age) {
            alert("Veuillez remplir les champs vide")
        } else {
            Axios.post("http://localhost:8000/create",
                {
                    nom: nom,
                    prenom: prenom,
                    date: date,
                    age: age,

                }).then(() => {
                    setNom("")
                    setAge("")
                    setPrenom("")
                    alert("Success")
                }

                ).catch((err) => {
                    console.log(err);
                })
        }
    }
    const deletePers = (id) => {
        Axios.delete(`http://localhost:8000/delete/${id}`).then(() => {
            alert("deleted")
            setList(list.filter((v) => {

                return v.list == id
            }))
        }).catch((err) => {
            console.log(err);
        })
    }

    const manova = (nom, prenom, age, id) => {
        seteditNom(nom)
        seteditId(id)
        seteditPrenom(prenom)
        seteditAge(age)
        handleShow()
    }
    const updatePers = () => {
        Axios.put("http://localhost:8000/update", {
            id: editId,
            nom: editNom,
            prenom: editPrenom,
            age: editAge
        }).then(() => {
            alert("Updated")
            handleClose(true)
            console.log("Success");
        }).catch((err) => {
            console.log(err);
        })
    }

    return (
        <div className='container'>
            <h3>CREATE</h3>
            <NavLink to='/'><button type="button" className="btn btn-dark">Accueil</button></NavLink> <br />

            <label htmlFor="">Nom:</label>  <input type="text" placeholder='Nom' value={nom} onChange={HandleChangenom} name='nom' /> <br />
            <label htmlFor="">Prenom:</label><input type="text" placeholder='Prenom' value={prenom} onChange={HandleChangeprenom} name='prenom' /> <br />
            <label htmlFor="">Date:</label>  <input type="date" placeholder='' value={date} onChange={HandleChangedate} name='date' /> <br />
            <label htmlFor="">Age:</label> <input type="number" placeholder='Age' value={age} onChange={HandleChangeage} name='age' /><br />
            <br />
            <button onClick={addPers} type="button" class="btn btn-success">Ajouter</button>
            <Table striped bordered hover size="sm">
                <thead>
                    <tr>

                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>age</th>
                        <th>Action</th>
                    </tr>

                    {list.map((l) => (
                        <tr>
                            <td>{l.nom}</td>
                            <td>{l.prenom}</td>
                            <td>{l.age}</td>
                            <td> <button class="btn btn-danger" onClick={() => deletePers(l.id)}>delete</button><button onClick={() => manova(l.nom, l.prenom, l.age, l.id)} className="btn btn-info">update</button> </td>
                        </tr>
                    )
                    )}

                </thead>

            </Table>
            <h3>Update</h3>


            {
                list.map((v) =>
                    <div key={v.id}>

                        <div class="card" style={{ width: 18 + 'rem' }}>
                            <div className="card-body">
                                <h5 class="card-title">Personnes</h5>
                                <p class="card-text">
                                    <h2> <u>Nom:</u> {v.nom}  <br /> <u>Prenom(s):</u> {v.prenom} <br /> <u>Age:</u>{v.age}</h2>   <button class="btn btn-danger" onClick={() => deletePers(v.id)}>delete</button><button onClick={() => manova(v.nom, v.prenom, v.age, v.id)} className="btn btn-info">update</button>
                                </p>
                            </div>
                        </div>
                    </div>


                )
            }


            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Update Modal</Modal.Title>
                </Modal.Header>
                <Modal.Body> <input type="hidden" placeholder='id' value={editId} onChange={(e) => seteditId(e.target.value)} name='nom' /> <br />
                    <label htmlFor="">Nom:</label>  <input type="text" placeholder='UpdateNom' value={editNom} onChange={(e) => seteditNom(e.target.value)} name='nom' /> <br />
                    <label htmlFor="">Prenom:</label><input type="text" placeholder='UpdatePrenom' value={editPrenom} onChange={(e) => seteditPrenom(e.target.value)} name='prenom' /> <br />
                    <label htmlFor="">Date:</label>  <input type="date" placeholder='' value={date} onChange={HandleChangedate} name='date' /> <br />
                    <label htmlFor="">Age:</label> <input type="number" placeholder='UpdateAge' value={editAge} onChange={(e) => seteditAge(e.target.value)} name='age' /><br />
                    <br /></Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" onClick={updatePers}>
                        Save Changes
                    </Button>
                </Modal.Footer>
            </Modal>





        </div>







    );
};

export default Create;
