import React, { useState } from 'react';
import Navigation from '../navigation/Navigation';
import "../style.css"
const Calculatrice = () => {

    //ho0ks
    const [valiny, setValiny] = useState("")
    const [soratra, setSoratra] = useState("test")

    //mamafa tsikelikely
    const mamafakely = () => {
        setValiny(valiny.slice(0, valiny.length - 1))
    }



    const handleClick = (e) => {
        //console.log(e.currentTarget.id);
        setValiny(valiny.concat(e.currentTarget.id))
    }

    //Mamafa resultat
    const mamafa = () => {
        setValiny("")
    }
    //Calcul

    const calculer = (e) => {
        setValiny(eval(valiny))
    }
    return (
        <div>
            <h3>Calculatrice</h3>
            <Navigation />

            <div className="calcul">
                <p className="result">  {valiny} <button onClick={mamafakely}>X</button></p>
                <button onClick={handleClick} id="9">9</button>
                <button onClick={handleClick} id="8">8</button>
                <button onClick={handleClick} id="7">7</button>
                <button onClick={handleClick} id="6">6</button>
                <button onClick={handleClick} id="5">5</button>
                <button onClick={handleClick} id="4">4</button>
                <button onClick={handleClick} id="3">3</button>
                <button onClick={handleClick} id="2">2</button>
                <button onClick={handleClick} id="1">1</button>
                <button onClick={handleClick} id="0">0</button>
                <button onClick={mamafa} id="clear">C</button>
                <button onClick={handleClick} id=".">.</button>
                <button onClick={handleClick} id="+">+</button>
                <button onClick={handleClick} id="-">-</button>
                <button onClick={handleClick} id="/">/</button>
                <button onClick={calculer} id="equal">=</button>

            </div>



            <input value={soratra} type="text" />
            {soratra}
        </div>
    );
};

export default Calculatrice;
