import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';

const Message = () => {
    const [message, setMessage] = useState('');
    const [mess, setMess] = useState([])
    const handleChange = (e) => {
        setMessage(e.target.value);
    };

    const sendMessage = async () => {
        const sender_id = 1;
        const recipient_id = 4;
        const content = message;

        try {
            const response = await axios.post('http://localhost:8000/message', {
                sender_id: sender_id,
                recipient_id: recipient_id,
                content: content
            }).then((response) => {
                if (response.data.success) {
                    setMessage("")
                }
            }).catch((error) => {
                console.log(error);
            })

            console.log('Le message a été envoyé:', response.data);
        } catch (error) {
            console.error(error);
        }
    };
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:8000/message/4');
                setMess(response.data);

            } catch (error) {
                console.error(error);
            }
        };
        fetchMessages();
    }, [mess]);

    return (
        <div>
            <div style={{ marginTop: '100px' }}></div>
            <Navbare />
            <h3>Message</h3>

            <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="10"></textarea> <br />
            <button onClick={sendMessage}>Envoyer</button>
            {mess.map((m) => (
                <div  key={m.id}>
                    <p style={{background:"blue", color:'white',height:'50px',width:'500px'}}> {m.content}</p>
                </div>
            ))}
        </div>
    );
};

export default Message;
