import axios from 'axios';
import { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';


const Create =()=> {
  const [show, setShow] = useState(false);
  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
  const [nom, setNom] = useState('');
  const [prenom, setPrenom] = useState('');
  const [dateNaissance, setDateNaissance] = useState('');

const ajouter=(e)=>{
    e.preventDefault()
    axios.post("http://localhost:8000/createOne",(nom,prenom,dateNaissance)).then(()=>{

    }).catch(()=>{
        
    })

}

  return (
    <>
      <Button variant="primary" onClick={handleShow}>
        Ouvrir le modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Entrer les informations</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Form>
            <Form.Group controlId="nom">
              <Form.Label>Nom</Form.Label>
              <Form.Control type="text" value={nom} onChange={e => setNom(e.target.value)} />
            </Form.Group>

            <Form.Group controlId="prenom">
              <Form.Label>Pr√©nom</Form.Label>
              <Form.Control type="text" value={prenom} onChange={e => setPrenom(e.target.value)} />
            </Form.Group>

            <Form.Group controlId="dateNaissance">
              <Form.Label>Date de naissance</Form.Label>
              <Form.Control type="date" value={dateNaissance} onChange={e => setDateNaissance(e.target.value)} />
            </Form.Group>

            <Button variant="secondary" onClick={handleClose}>
              Fermer
            </Button>
            <Button variant="primary" onClick={ajouter} type="submit">
              Enregistrer les changements
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default Create;
