import React from 'react';
import Navbare from './Navbare';
import { useState, useEffect } from 'react';
import Button from 'react-bootstrap/Button';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Card from 'react-bootstrap/Card';
import axios from 'axios';
import Form from 'react-bootstrap/Form';
const Accueil = () => {


    const userId = sessionStorage.getItem('userId');
    const username = sessionStorage.getItem('username');
    const [like, setLike] = useState(0);
    const [liked, setLiked] = useState(false);

    const [selectedImage, setSelectedImage] = useState(null);
    const [pub, setPub] = useState("");
    const [pubs, setPubs] = useState([]);



    const mampiakatra = () => {
        if (liked) {
            return;

        }
        setLike(like + 1);
        setLiked(true)
    }

    useEffect(() => {
        afficherPub();
    }, [pub]);

    const afficherPub = async () => {
        try {
          //  const response = await axios.post('http://localhost:8000/makapub');
          const response = await axios.get('http://localhost:8000/makapub');
            setPubs(response.data);
            console.log(pubs);
            pubs.map((p) => {
                console.log(p.sender_id);
            }
            )
            console.log(pubs[0].sender_id);
        } catch (error) {
            console.error("Erreur lors de la récupération de la publication :", error);
        }
    };



    const publier = (e) => {

        e.preventDefault();
        console.log(pub);
        try {
            axios.post('http://localhost:8000/publication', {
                pub: pub,
                sender_id: userId
            }).then((response) => {
                setPub("")
                if (response.data.success) {
                    toast.success('Bien publiée', {
                        position: "top-center",
                        autoClose: 2000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        theme: "light",
                    });
                }

            }).catch(() => {

            })
        } catch (error) {

        }

    }
    return (
        <div>
            <div style={{ marginTop: '100px' }}></div>
            <Navbare />
            <div>
                <h2>Créer une publication</h2>
                <form>
                    <textarea value={pub} onChange={(e) => setPub(e.target.value)} rows="4" cols="50" placeholder="Exprimez-vous..."></textarea>

                    <br />
                    <input type="file" accept="image/*" />
                    <br />
                    <br />
                    <button onClick={publier}>Publier</button>
                </form>
            </div>








            <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', minHeight: '100vh' }}>
                {pubs.map((pubs) => (


                    <Card key={pubs.id} style={{ margin: '10px' }}>
                        <Card.Header>Publication</Card.Header>
                        <Card.Body>
                            <Card.Title>Special title treatment{pubs.sender_id}</Card.Title>
                            <Card.Text>
                                {pubs.texte} <br />
                                <Form.Control as="textarea" rows={4} />

                            </Card.Text>
                            <Button variant="primary" disabled={liked} onClick={mampiakatra}>LIke {like}</Button>     <Button variant="primary">Comment </Button>
                        </Card.Body>
                    </Card>

                ))}

            </div>



        </div>
    );
};

export default Accueil;
