        <div>
            <p>{clients.map((client) => <li>{client.nom} <button onClick={() => mamafa(client.id)}>X</button> </li>)}</p>


            <form action="" onSubmit={handleSubmit}>
                <input onChange={handleChange} value={nouveauClient} type="text" placeholder='Entre un nouveau client' /> <button>Ajouter +</button>
                {nouveauClient}
            </form>

        </div >
    );
};

export default App;
