import React from 'react';
import axios from 'axios';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
const AddLivre = () => {

    const [item, setItem] = useState({
        title: "",
        description: ""
    })
    const handleChange = (e) => {
        const copy = { ...item }
        copy[e.target.id] = e.target.value
        setItem(copy)
        console.log(copy);
    }
    const add = (e) => {

        const newItem = {
            title: item.title,
            description: item.description
        }

        axios.post('/newitem', newItem)
            .then((res) => console.log(res))
            .catch((err) => console.log(err))

        console.log(newItem);
        setItem({
            title: "",
            description: ""
        })
    }
    return (
        <div>

            <Form>
                <Form.Group className="mb-3" controlId="formBasicEmail">
                    <Form.Label>Email address</Form.Label>
                    <Form.Control type="email" placeholder="Enter email" />
                    <Form.Text className="text-muted">
                        We'll never share your email with anyone else.
                    </Form.Text>
                </Form.Group>

                <Form.Group className="mb-3" controlId="formBasicPassword">
                    <Form.Label>Password</Form.Label>
                    <Form.Control type="password" placeholder="Password" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="formBasicCheckbox">
                    <Form.Check type="checkbox" label="Check me out" />
                </Form.Group>
                <Button variant="primary" type="submit">
                    Submit
                </Button>
            </Form>

            <input id='title' onChange={handleChange} type="text" name="title" value={item.title} placeholder='Entrer Title' />
            <input id='description' onChange={handleChange} type="text" name="description" value={item.description} placeholder='Entrer description' />
            <button onClick={add}>Ajouter</button>
        </div>
    );
};

export default AddLivre;
