import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';


const Message = (props) => {
    const [message, setMessage] = useState('');
    const [mess, setMess] = useState([])
    const [user, setUser] = useState(null);
    const [list, setList] = useState([])
    const handleChange = (e) => {
        setMessage(e.target.value);
    };


    const fetchUser = async () => {
        try {
            await axios.get('http://localhost:8000/getUser')
                .then((response) => {
                    setList(response.data);
                }).catch((error) => {
                    console.log(error);
                })
        } catch (error) {
            console.log(error);
        }
    }






    const sendMessage = async () => {
        const sender_id = 1;
        const recipient_id = 4;
        const content = message;

        try {
            const response = await axios.post('http://localhost:8000/message', {
                sender_id: sender_id,
                recipient_id: recipient_id,
                content: content
            }).then((response) => {
                if (response.data.success) {
                    setMessage("")
                }
            }).catch((error) => {
                console.log(error);
            })

            console.log('Le message a été envoyé:', message);
        } catch (error) {
            console.error(error);
        }
    };
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:8000/message/4');
                setMess(response.data);
            } catch (error) {
                console.error(error);
            }
        };
        fetchMessages();
    }, [message]);

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-8">
                    <div style={{ marginTop: '100px' }}></div>
                    <Navbare />
                    <div style={{ position: 'fixed' }}>
                        <h3>Message <button onClick={fetchUser}>test</button></h3>
                        <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="1"></textarea> <br />
                    </div>


                    {mess.map((m) => (
                        <div style={{ borderRadius: '100px' }} key={m.id}>
                            <p></p>
                            <p style={{ background: "blue", color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center' }}> {m.content}</p>
                            <p style={{ fontSize: '14px' }}>{m.createdAt}</p>
                        </div>
                    ))}

                    <button onClick={sendMessage}>Envoyer</button>
                </div>

                <div className="col-4">
                    <div   style={{ marginTop: '100px' , position}}></div>
                    <h3>Liste des utilisateurs</h3>
                    {list.map((user) => (
                        <li key={user.id}> {user.id} - {user.login}</li>
                    ))}

                </div>
            </div>
        </div>

    );
};

export default Message;
