import React, { useState } from 'react';
const App = (props) => {



    const [clients, setClients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])

    const mamafa = (id) => {

        const idFafana = clients.filter((client) =>
            client.id !== id
        )
        console.log(id);
        setClients(idFafana)
    }
    const [nouveauClient, setnouveauClient] = useState("")
    const ekena = (e) => {
        e.preventDefault()
        const copyClients = [...clients]


        const id = new Date().getTime()
        const nom = nouveauClient
        copyClients.push({ id: id, nom: nom })
        setClients(copyClients)

    }


    const handleChange = (e) => {
        setnouveauClient(e.currentTarget.value)
    }
    return (
        <div>
            {clients.map((client) =>
                <li>{client.nom} <button onClick={() => mamafa(client.id)}>X</button></li>
            )
            }


            <form action="" onSubmit={ekena}>
                <input onChange={handleChange} value={nouveauClient} type="text" placeholder='Ajouter un client'

                />  <button>Ajouter +</button>
            </form>
        </div >
    );
};

export default App;
