import React, { useState } from 'react';
import Axios from 'axios';
import { NavLink } from 'react-router-dom';


const Create = () => {
    const [list, setList] = useState([])
    const [nom, setNom] = useState("")
    const [prenom, setPrenom] = useState("")
    const [age, setAge] = useState(0)

    const HandleChangenom = (e) => {
        setNom(e.target.value)
    }
    const HandleChangeprenom = (e) => {
        setPrenom(e.target.value)
    }
    const HandleChangeage = (e) => {
        setAge(e.target.value)
    }

    const addPers = () => {
        Axios.post('http://localhost:8000/create',
            {
                nom: nom,
                prenom: prenom,
                age: age
            });
        try {
            alert("success")
        } catch (error) {
            alert(error)

        }
    }
    const showPers = () => {
        Axios.get('http://localhost:8000/afficher').then((response) => {
            setList(response.data)
            console.log(response.data);
        });

        try {
            alert()
        } catch (error) {
            alert(error)
        }
    }
    return (
        <div>
            <h3>CREATE</h3>
            <NavLink to='/'>Accueil</NavLink> <br />
            <input type="text" placeholder='Nom' value={nom} onChange={HandleChangenom} name='nom' />
            <input type="text" placeholder='Prenom' value={prenom} onChange={HandleChangeprenom} name='prenom' />
            <input type="number" placeholder='Age' value={age} onChange={HandleChangeage} name='age' /><br />
            <button onClick={addPers}>Ajouter</button><br />
            <button onClick={showPers}>Show</button>
            <p>a {nom}</p>
            {list.map((value, key) => {
                return (
                    <div><p> aaaaa {value.age}</p></div>
                )
            })}
        </div >
    );
};

export default Create;
