import React, { useState } from 'react';

import Client from './Client';
const App = (props) => {

    const [nouveauClients, setNouveauclients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])

    const handleChange = (e) => {
        setNouveauclients(e.currentTarget.value)
    }

    const HandleDelete = (id) => {
        const update = [...nouveauClients]
        const index = update.findIndex(client => nouveauClients.id === id)
        update.splice(index, 1)
        setNouveauclients(update)
    }
    const HandleSubmit = (e) => {
        e.preventDefault();
        const id = new Date().getTime()
        const nom = nouveauClients
        props.onClientAdd({ id, nom })
        setNouveauclients("")
    }

    return (
        <div>

            {nouveauClients.map(nouveauClients =>
                <Client
                    key={nouveauClients.id}
                    details={nouveauClients}
                    onDelete={HandleDelete}
                />
            )}




        </div>
    );
};

export default App;
