import React, { useState } from 'react';
import Client from './Client';
import ClientForm from './ClientForm';
const App = (props) => {

    const [clients, setClients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])



    const HandleDelete = (id) => {
        const updatedClient = [...clients]
        const index = updatedClient.findIndex(client => client.id === id)
        updatedClient.splice(index, 1)
        setClients(updatedClient)
    }

    const handleAdd = (client) => {
        const updatedClient = [...clients]
        updatedClient.push(client)
        setClients(updatedClient)
    }


    return (
        <div>

            {clients.map(client =>
                <Client
                    key={client.id}
                    details={client}
                    onDelete={HandleDelete}
                />
            )}


            <ClientForm onClientAdd={handleAdd} />

        </div>
    );
};

export default App;
