import React, { useNavigate, useState } from 'react';
import axios from 'axios';
import { NavLink } from 'react-router-dom';
const navigate = useNavigate()
const Create = () => {
    const [pers, setPers] = useState({
        'nom': "",
        'prenom': "",
        'age': null
    })
    const HandleChange = (e) => {
        setPers((prev) => ({ ...prev, [e.target.value]: e.target.value }));
    }
    const HandleSubmit = (event) => {
        event.preventDefault();
        try {
            axios.post("http://localhost:8000/books", pers)
            navigate("/")
        } catch (error) {
            console.log(err);
        }
    }
    return (
        <div>
            <h3>CREATE</h3>
            <NavLink to='/'>Accueil</NavLink> <br />
            <input type="text" placeholder='Nom' onChange={HandleChange} name='nom' />
            <input type="text" placeholder='Prenom' onChange={HandleChange} name='prenom' />
            <input type="number" placeholder='Age' onChange={HandleChange} name='age' /><br />
            <button onSubmit={HandleSubmit} >Ajouter</button>
        </div >
    );
};

export default Create;
