import React from 'react';
import "./style.css"
import Navigation from './nav/Navigation';
import { useState } from 'react';
const Calculatrice = () => {

    const [valiny, setValiny] = useState("")

    const calcul = document.querySelector("#equal")
    calcul.addEventListener("click", (e) => {
        setValiny(eval(valiny.concat(e.currentTarget.id)))
    })

    const handleChange = (e) => {
        setValiny(valiny.concat(e.currentTarget.id))
    }

    return (
        <div>

            <h1>Calculatrice</h1>
            <Navigation />

            <div className="vatana">
                <h3 className="resultat"> {valiny}</h3>
                <button className="btn" onClick={handleChange} id="9">9</button>
                <button className="btn" onClick={handleChange} id="8">8</button>
                <button className="btn" onClick={handleChange} id="7">7</button>
                <button className="btn" id="6">6</button>
                <button className="btn" id="5">5</button>
                <button className="btn" id="4">4</button>
                <button className="btn" id="3">3</button>
                <button className="btn" id="2">2</button>
                <button className="btn" id="1">1</button>
                <button className="btn" id="0">0</button>
                <button className="btn" id=".">.</button>
                <button className="btn" onClick={handleChange} id="+">+</button>
                <button className="btn" id="-">-</button>
                <button className="btn" id="/">/</button>
                <button className="btn" id="*">*</button>
                <button className="btn" id="clear">C</button>
                <button className="btn" id="equal">=</button>
            </div>
        </div>
    );
};

export default Calculatrice;
