import React, { useState } from 'react';
const App = (props) => {



    const [clients, setClients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])

    const mamafa = (id) => {
        const kil = clients.filter((client) =>
            client.id !== id
        )
        setClients(kil)
    }
    const [nouveauClient, setnouveauClient] = useState("")
    const handleSubmite = (e) => {
        e.preventDefault()
        const copyClient = [...clients]
        const id = new Date().getTime()
        const nom = nouveauClient

        if (nom === "") {
            alert("on")
        }
        copyClient.push({ id: id, nom: nom })
        setClients(copyClient)
        console.log(id);
        setnouveauClient("")
    }





    const handeChange = (e) => {
        setnouveauClient(e.currentTarget.value)
    }
    return (
        <div>
            {clients.map((client) =>
                <li>{client.nom} <button onClick={() => mamafa(client.id)}>X</button></li>
            )
            }
            <form action="" onSubmit={handleSubmite}>
                <input value={nouveauClient} onChange={handeChange}
                    type="text" placeholder='Entre un nouveau client' /><button>ajouter +</button>
            </form>


        </div >
    );
};

export default App;
