import React, { useEffect, useState } from 'react';
import Axios from 'axios';
import { NavLink } from 'react-router-dom';


const Create = () => {
    const [list, setList] = useState([])
    const [nom, setNom] = useState("")
    const [prenom, setPrenom] = useState("")
    const [date, setDate] = useState("")
    const [age, setAge] = useState()

    useEffect(() => {

        Axios.get("http://localhost:8000/afficher").then((response) => {
            setList(response.data);
        })

    })

    const HandleChangenom = (e) => {
        setNom(e.target.value)
    }

    const HandleChangeprenom = (e) => {
        setPrenom(e.target.value)
    }
    const HandleChangeage = (e) => {
        setAge(e.target.value)
    }
    const HandleChangedate = (e) => {
        setDate(e.target.value)
    }

    const [editNom, seteditNom] = useState("")
    const [editPrenom, seteditPrenom] = useState("")
    const [editAge, seteditAge] = useState()

    const addPers = () => {
        if (!nom || !prenom || !age) {
            alert("Veuillez remplir les champs vide")
        } else {
            Axios.post("http://localhost:8000/create",
                {
                    nom: nom,
                    prenom: prenom,
                    date: date,
                    age: age,

                }).then(() => {
                    setNom("")
                    setAge("")
                    setPrenom("")
                    console.log("Success");

                }

                ).catch((err) => {
                    console.log(err);
                })
        }
    }
    const deletePers = (id) => {
        Axios.delete(`http://localhost:8000/delete/${id}`).then(() => {
            setList(list.filter((v) => {
                //   return v.list == id
            }))
        }).catch((err) => {
            console.log(err);
        })
    }



    return (
        <div className='container'>
            <h3>CREATE</h3>
            <NavLink to='/'><button type="button" class="btn btn-dark">Accueil</button></NavLink> <br />

            <label htmlFor="">Nom:</label>  <input type="text" placeholder='Nom' value={nom} onChange={HandleChangenom} name='nom' /> <br />
            <label htmlFor="">Prenom:</label><input type="text" placeholder='Prenom' value={prenom} onChange={HandleChangeprenom} name='prenom' /> <br />
            <label htmlFor="">Date:</label>  <input type="date" placeholder='' value={date} onChange={HandleChangedate} name='date' /> <br />
            <label htmlFor="">Age:</label> <input type="number" placeholder='Age' value={age} onChange={HandleChangeage} name='age' /><br />
            <br />
            <button onClick={addPers} type="button" class="btn btn-success">Ajouter</button>
            <h3>Update</h3>
            <label htmlFor="">Nom:</label>  <input type="text" placeholder='UpdateNom' value={editNom} onChange={HandleChangenom} name='nom' /> <br />
            <label htmlFor="">Prenom:</label><input type="text" placeholder='UpdatePrenom' value={editPrenom} onChange={HandleChangeprenom} name='prenom' /> <br />
            <label htmlFor="">Date:</label>  <input type="date" placeholder='' value={date} onChange={HandleChangedate} name='date' /> <br />
            <label htmlFor="">Age:</label> <input type="number" placeholder='UpdateAge' value={editAge} onChange={HandleChangeage} name='age' /><br />
            <br />
            <button onClick={addPers} type="button" class="btn btn-success">Update</button>

            {
                list.map((v) =>
                    <div key={v.id}>
                        <div class="card" style={{ width: 18 + 'rem' }}>
                            <div className="card-body">
                                <h5 class="card-title">Personnes</h5>
                                <p class="card-text">
                                    <h2> <u>Nom:</u> {v.nom}  <br /> <u>Prenom(s):</u> {v.prenom} <br /> <u>Age:</u>{v.age}</h2> <button class="btn btn-danger" onClick={() => deletePers(v.id)}>delete</button><button id="btn_update" className="btn btn-info">update</button>
                                </p>

                            </div>
                        </div>
                    </div>


                )
            }



            <div className="modal" id="exampleModale" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog">
                    <div class="modal-content">
                        <div class="modal-header">
