import React, { useState } from 'react';
import { Navbar, Nav, Container } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import Deconnexion from './Deconnexion';
import Message from './Message';
import Cookies from 'js-cookie';


const Navbare = (props) => {

  const handleClick = () => {
    const response = Deconnexion.deco();
    console.log(response);
    // Mettre à jour l'état de l'application ou rediriger l'utilisateur vers une page de connexion, etc.
  };



  const afficherSession = () => {
    fetch('/session')
      .then(response => response.json())
      .then(data => console.log(data));
  };
  
  return (
    <div>
      <Navbar bg="light" expand="lg" fixed="top">
        <Container>
          <Navbar.Brand href="#" style={{ color: 'blue', fontSize: '40px', marginRight: '600px' }}>
            E-Tafa {props.nom} <button onClick={afficherSession}>test</button>
          </Navbar.Brand>

          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="me-auto">
              <NavLink
                to="/accueil"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Accueil
              </NavLink>
              <NavLink
                to="/message"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Message
              </NavLink>
              <NavLink
                to="/contact"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                Contact
              </NavLink>
              <NavLink
                to="/about"
                style={{ color: 'black', fontSize: '20px', textDecoration: 'none', borderBottom: 'none', marginRight: '35px' }}
              >
                A propos
              </NavLink>

              <button onClick={handleClick}>Déconnexion</button>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>

    </div>
  );
};

export default Navbare;
