import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';
import Create from './Message';


const userId = sessionStorage.getItem('userId');
const username = sessionStorage.getItem('username');

const Message = () => {
  const [message, setMessage] = useState('');
  const [mess, setMess] = useState([]);
  const [user, setUser] = useState(null);
  const [list, setList] = useState([]);

  const handleChange = (e) => {
    setMessage(e.target.value);
  };

  const fetchUser = async () => {

    try {
      const response = await axios.get('http://localhost:8000/getUser');
      setList(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const fetchMessages = async (userId) => {
    try {

      const response = await axios.get(`http://localhost:8000/message/${userId}`);
      setMess(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const afficher = (id) => {
    if (message !== '') {
      sendMessage(id);
    }
    setUser(id);
    sendMessage(id);
    fetchMessages(id);
  };

  const sendMessage = async (id) => {
    console.log("L'ID de l'utilisateur est : " + id);
    const recipient_id = 4;
    const content = message;

    try {
      const response = await axios.post('http://localhost:8000/message', {
        sender_id: userId,
        recipient_id: recipient_id,
        content: content,
      });
      if (response.data.success) {
        setMessage('');
      }
      console.log('Le message a été envoyé:', message);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (user) {
      fetchMessages(user);
    }
  }, [user]);

  return (

    <div className="container-fluid">
      <div className="row">
        <div className="col-8">
          <div style={{ marginTop: '100px' }}></div>
          <Navbare />
          <div style={{ position: 'fixed' }}>
            <h3>Message</h3> <button onClick={fetchUser}>test</button> 
            <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="1"></textarea>{' '}

            <br />
            <button onClick={sendMessage}>Envoyer</button>
            <input type="text" /> 
          </div>

          {mess.map((m) => (
            <div style={{ borderRadius: '100px' }} key={m.id}>
              <p></p>
              <p style={{ background: 'blue', color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center' }}>
                {' '}
                {m.content}
              </p>
              <p style={{ fontSize: '14px' }}>{m.createdAt}</p>
            </div>
          ))}


        </div>

        <div className="col-4">
          <div style={{ marginTop: '100px' }}></div>
          <h3>Liste des utilisateurs</h3>
          {list.map((user) => (


            <li onClick={() => afficher(user.id)} key={user.id}> {user.id} - {user.login}</li>
          ))}

        </div>
      </div>

    </div>

  );
};

export default Message;
