import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';

const Message = (props) => {
    const [message, setMessage] = useState('');
    const [mess, setMess] = useState([])
    const [user, setUser] = useState(null);
    const [list, setList] = useState([])
    const handleChange = (e) => {
        setMessage(e.target.value);
    };


    const fetchUser = async () => {
              const users = await axios.get
    }


    const sendMessage = async () => {
        const sender_id = 1;
        const recipient_id = 4;
        const content = message;

        try {
            const response = await axios.post('http://localhost:8000/message', {
                sender_id: sender_id,
                recipient_id: recipient_id,
                content: content
            }).then((response) => {
                if (response.data.success) {
                    setMessage("")
                }
            }).catch((error) => {
                console.log(error);
            })

            console.log('Le message a été envoyé:', message);
        } catch (error) {
            console.error(error);
        }
    };
    useEffect(() => {
        const fetchMessages = async () => {
            try {
                const response = await axios.get('http://localhost:8000/message/4');
                setMess(response.data);


                // Récupération de l'utilisateur
                // const userResponse = await axios.get('http://localhost:8000/user/1');
                // setUser(userResponse.data);

            } catch (error) {
                console.error(error);
            }
        };
        fetchMessages();
    }, [message]);

    return (
        <div >
            <div style={{ marginTop: '100px' }}></div>
            <Navbare />
            <div style={{ position: 'fixed' }}><h3>Message</h3>
                <textarea value={message} onChange={handleChange} name="" id="" cols="30" rows="1"></textarea> <br /></div>

            {mess.map((m) => (
                <div style={{ borderRadius: '100px' }} key={m.id}>
                    <p></p>
                    <p style={{ background: "blue", color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center' }}> {m.content}</p>
                    <p style={{ fontSize: '14px' }}>{m.createdAt}</p>
                </div>
            ))}



            <button onClick={sendMessage}>Envoyer</button>
        </div>
    );
};

export default Message;
