import React, { useState } from 'react';
const App = (props) => {



    const [clients, setClients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])

    const mamafa = (id) => {
        const kil = clients.filter((client) =>
            client.id !== id
        )
        setClients(kil)
    }



    return (
        <div>
            {clients.map((client) =>
                <li>{client.nom} <button onClick={() => mamafa(client.id)}>X</button></li>
            )
            }



        </div >
    );
};

export default App;
