import React, { useState } from 'react';
import axios from 'axios';
const App = (props) => {



    const [clients, setClients] = useState([
        { id: 1, nom: "Marco Henikaja" },
        { id: 2, nom: "Rakoto Bogosy" },
        { id: 3, nom: "Randria Vendrana" },
    ])

    const mamafaListe = (id) => {
        const liste = clients.filter((client) =>
            client.id !== id
        )
        setClients(liste)
    }



    const HandleSubmit = (e) => {
        e.preventDefault()
    }



    return (
        <div>
            {clients.map((client) =>
                <li>{client.id}  ...  {client.nom}  <button onClick={() => mamafaListe(client.id)}>X</button></li>
            )
            }

            <form onSubmit={HandleSubmit} >
                <input type="text" placeholder='entrer un client' /> <button>Ajouter +</button>
            </form>

        </div >
    );
};

export default App;
