import React, { useState } from 'react';
import Navigation from '../nav/Navigation';
const Incrementer = () => {
    const [nombre, setNombre] = useState(0)
    const [mivova, setMiova] = useState("")
    const incr = () => {
        setNombre(nombre + 1)
    }
    const oNSubmit = (e) => {
        e.preventDefault();
    }
    const handleChange = (e) => {
        setMiova(e.currentTarget.value)
    }
    const [clients, setClients] = useState(
        [
            { id: "1", nom: "marco" },
            { id: "2", nom: "Makooto" }
        ])

    return (
        <div>
            <h3>Incrementation</h3>
            <Navigation />
            <p>{nombre} <button onClick={incr} >INcrementer</button></p>
            <p>
                {clients.map((client) =>
                    <li>{client.nom}</li>
                )}
            </p>


            <input type="text"
            /> <button onSubmit={oNSubmit} value={mivova} onClick={handleChange}>valider</button>
            <p>{mivova}</p>
        </div>
    );
};

export default Incrementer;
