import React, { useState } from 'react';
import axios from 'axios';
const App = (props) => {

    const [clients, setClients] = useState([
        { id: 1, nom: "RAKOTOBE Marco" },
        { id: 2, nom: "RANDRIA BG" },
        { id: 3, nom: "RAJAO Froncois" },
        { id: 4, nom: "DOKOTERA Bao" }
    ])

    const handleDelete = (id) => {
        const clientFilter = clients.filter((client) =>
            client.id !== id
        )
        setClients(clientFilter)
    }

    return (
        <div>

            <p>
                {clients.map((client) => <li> {client.nom} <button onClick={() => handleDelete(client.id)}>X</button></li>)}
                <form action="">
                    <input type="text" name="" id="" />
                </form>
            </p>
        </div >
    );
};

export default App;
