import React, { useEffect, useState } from 'react';
import Navbare from './Navbare';
import axios from 'axios';
import Create from './Message';
import { ToastContainer, toast } from 'react-toastify';

const userId = sessionStorage.getItem('userId');
const username = sessionStorage.getItem('username');

const Message = () => {
  const [message, setMessage] = useState('');
  const [mess, setMess] = useState([]);
  const [user, setUser] = useState(null);
  const [list, setList] = useState([]);
  const [temp, setTemp] = useState('');
  const [anarana, setAnarana] = useState('')

  const handleChange = (e) => {
    setMessage(e.target.value);
  };

  useEffect(() => {
    const fetchUser = async () => {

      try {
        const response = await axios.get(`http://localhost:8000/getUser/${userId}`);
        setList(response.data);
      } catch (error) {
        console.log(error);
      }
    };
    fetchUser();
  }, [mess]);

  const afficher = (id, ana) => {
    setAnarana(ana)
    setTemp(id);
    setUser(id);
    fetchMessages(id);//id recipient_id ito 
  };

  const fetchMessages = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8000/message/${id}/${userId}`);
      setMess(response.data);
      console.log(response.data);
    } catch (error) {
      console.error(error);
    }
  };



  const sendMessage = async (e) => {
    setTemp(e.target.value);
    if (temp === '') {
      toast.error('Veuillez sélectionner un destinataire', {
        position: "top-center",
        autoClose: 2000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "light",
      });
      return;
    }

    try {
      const response = await axios.post('http://localhost:8000/message', {
        sender_id: userId,
        recipient_id: temp,
        content: message,
      });
      if (response.data.success) {
        const response = await axios.get(`http://localhost:8000/message/${temp}/${userId}`);
        setMess(response.data);
        setMessage('');
      }
      console.log('Le message a été envoyé:', message);
    } catch (error) {
      console.error(error);
    }
  };



  return (

    <div className="container-fluid">
      <div className="row">
        <div className="col-8 mx-auto my-auto text-center">
          <div style={{ marginTop: '100px' }}></div>
          <Navbare />
          <div style={{ position: 'fixed' }}>
            <h3 style={{ marginBottom: '10px' }}>Message</h3>
            <div style={{ display: 'flex' }}>
              <input style={{ marginRight: '10px' }} value={message} onChange={handleChange} type="text" />
              <button onClick={sendMessage}>Envoyer</button>
            </div>
            <input type="hidden" value={temp} />
          </div>




          <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
            <div style={{ borderRadius: '100px' }}>
              <div style={{ position: 'fixed', top: '100px', left: '50%', transform: 'translateX(-50%)' }}>
                <input value={anarana} type="hidden" />
                <p style={{ background: 'lightgray', color: 'black', padding: '10px', borderRadius: '8px', maxWidth: '300px', margin: '10px auto', textAlign: 'center', marginLeft: '-380px' }}>
                  Envoyé à :  {anarana}
                </p>

              </div>


              {mess.map((m) => (
                <div key={m.id}>

                  {m.sender_id != userId ? (
                    <div>
                      <p style={{ background: 'pink', color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center', marginLeft: '-90px' }}>
                        {m.content}
                      </p>
                      <p style={{ fontSize: '14px', marginLeft: '-300px' }}>{m.createdAt}</p>
                    </div>


                  ) : (
                    <div>
                      <p style={{ background: 'blue', color: 'white', height: '50px', width: '400px', borderRadius: '80px', textAlign: 'center', marginRight: '-900px' }}>
                        {m.content}
                      </p>
                      <p style={{ fontSize: '14px', margi: '-300px' }}>{m.createdAt}</p>
                    </div>

                  )}


                </div>
              ))}



            </div>
          </div>






        </div>

        <div className="col-4">
          <div style={{ marginTop: '100px' }}></div>
          <div style={{ position: 'fixed' }}>
            <h3>Liste des utilisateurs</h3>
            {list.map((user) => (
              <li onClick={() => afficher(user.id, user.login)} key={user.id}>  {user.id} - {user.login}</li>
            ))}
          </div>

        </div>
      </div>
      <ToastContainer />
    </div>


  );
};

export default Message;
