import React from 'react';
import axios from 'axios';
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
const AddLivre = () => {
    const [conteur, setCounteur] = useState("1");
    const ajouter = () => {
        setCounteur(conteur + 1)
    }

    return (
        <div>
            <form action="">
                <p>Addd livre</p>
                <input type="text" />
                <input type="text" />
                <br />
                <button onClick={ajouter} id="btn_ajouter">Ajouter</button>
                <p>{conteur}</p>
            </form>

        </div>
    );
};

export default AddLivre;
