import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import { Form } from 'react-bootstrap';
import Axios from 'axios';
const Users = () => {

    const [username, setUsername] = useState("")
    const [pass, setPass] = useState("")
    const handleChange = (e) => {
        setUsername(e.target.value)
    }
    const connecter = () => {
        Axios.post("http://localhost/connect",)
    }

    return (
        <div>
            <div className="container">
                <h3>Login</h3>
                <Form.Control size="lg" value={username} type="text" onChange={handleChange} placeholder="Username" style={{ width: 500 }} />
                <br />
                <Form.Control size="lg" value={pass} type="password" placeholder="Password" onChange={(e) => setPass(e.target.value)} style={{ width: 500 }} /> <br />
                <Button onClick={connecter} variant="info">Enregister</Button>
            </div>
        </div>
    );
};

export default Users;
