import React, { useState } from 'react';
import Navigation from '../nav/Navigation';
const Incrementer = () => {
    const [nombre, setNombre] = useState(0)
    const [miova, setMiova] = useState("s")
    const incr = () => {
        setNombre(nombre + 1)
    }



    const handleChange = () => {

    }

    const handleSubmit = (e) => {
        e.preventDefault();
    }

    const [clients, setClients] = useState(
        [
            { id: "1", nom: "marco" },
            { id: "2", nom: "Makooto" }
        ])

    return (
        <div>
            <h3>Incrementation</h3>
            <Navigation />
            <p>{nombre} <button onClick={incr} >INcrementer</button></p>
            <p>
                {clients.map((client) =>
                    <li>{client.nom}</li>
                )}
            </p>


            <input type="text" value={miova} onSubmit={handleSubmit}
                onChange={handleChange}

            />

            <p>{miova}</p>
        </div>
    );
};

export default Incrementer;
